package Demo;import java.awt.BorderLayout;import java.awt.EventQueue;import java.awt.FlowLayout;import javax.swing.JFrame;import javax.swing.JPanel;import javax.swing.JScrollPane;import javax.swing.JTable;import javax.swing.JTextField;import javax.swing.border.EmptyBorder;import javax.swing.table.DefaultTableModel;import db.DButil;import javax.swing.JLabel;import java.awt.Font;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.Connection;import java.sql.ResultSet;import java.sql.SQLException;import java.sql.Statement;import java.text.NumberFormat;import java.util.Locale;import java.util.Vector;import javax.swing.Box;import javax.swing.JButton;public class MainFrame extends JFrame {	private JPanel contentPane;	JLabel label;	JButton button,button_1;	public MainFrame() {		setVisible(true);		setTitle("客户端");		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		setBounds(100, 100, 450, 300);		getContentPane().setLayout(null);				 label = new JLabel("桶装水选购");		label.setFont(new Font("?1?7?1?7?1?7?1?7", Font.BOLD, 25));		label.setBounds(140, 33, 147, 33);		getContentPane().add(label);				button = new JButton("产品列表");		button.setBounds(140, 99, 113, 27);		getContentPane().add(button);				button_1 = new JButton("购买桶装水");		button_1.setBounds(140, 164, 113, 27);		getContentPane().add(button_1);		button_1.addActionListener(new ActionListener() {						@Override			public void actionPerformed(ActionEvent e) {				new OrderFrame();			}		});		button.addActionListener(new ActionListener() {			JFrame frame;			JLabel label;			Box box1,box2,box3;			JTable table;			@Override			public void actionPerformed(ActionEvent e) {				frame=new JFrame("浏览产品");				frame.setBounds(800, 250,600, 600);				frame.setLayout(new FlowLayout());				frame.setLayout(new FlowLayout());				SheJi();				frame.setVisible(true);				frame.setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);			}			void SheJi() {				label=new JLabel("桶装水列表");				Vector<Vector<Object>> contextList = new Vector<>();					Vector<Object> titleList = new Vector<>();				DefaultTableModel model;				model = new DefaultTableModel();				box1=Box.createVerticalBox();				box2=Box.createHorizontalBox();				box3=Box.createHorizontalBox();				box2.add(label);				table = new JTable(model);				JScrollPane js=new JScrollPane(table);				box3.add(js);				box1.add(box2);				box1.add(Box.createVerticalStrut(20));				box1.add(box3);				frame.add(box1);				DButil butil=new DButil();				Connection con=butil.getConnection();				Statement sql;				ResultSet rs;				Locale cnLocale=new Locale("zh", "CN");				NumberFormat cn=NumberFormat.getCurrencyInstance(cnLocale);				try {					sql=con.createStatement();					rs=sql.executeQuery("select * from [dbo].[Produce]");					while(rs.next()) {						Vector<Object> vector1 = new Vector<>();						for(int i=1;i<=3;i++)						{							if(i!=3)							vector1.add(rs.getString(i));							else							vector1.add(cn.format(Integer.parseInt(rs.getString(i))));						}						contextList.add(vector1);												}					titleList.add("编号");					titleList.add("产品名");					titleList.add("价格");										model.setDataVector(contextList, titleList);										table = new JTable(model);				}catch(SQLException e1) {									}							}		});	}}